package com.zy.util.error;/** * Created by joyce on 2018/01/12. */public class CommonApiException extends RuntimeException{    public CommonApiException() {        super();    }    public CommonApiException(Throwable cause) {        super(cause);    }    public CommonApiException(String message) {        super(message);    }    public CommonApiException(String message, Throwable cause) {        super(message, cause);    }    protected CommonApiException(String message, Throwable cause, boolean enableSuppression, boolean writableStackTrace) {        super(message, cause, enableSuppression, writableStackTrace);    }}